<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class notificacao extends B10_Model{    public function __construct(){        parent::__construct();    }        public function get_notificacoes_nao_lidas(){        return $this->get_all(array(            'usuario'   =>  $this->usuario->get_user('id'),            'lido'      =>  0,        ),array('data','desc'));    }        public function get_notificacoes(){        return $this->get_all(array(            'usuario'   =>  $this->usuario->get_user('id'),        ),array('data','desc'));    }        public function alterar_status($id,$status){        $where  =   array(            'id'        =>  $id,            'usuario'   =>  $this->usuario->get_user('id'),            'lido'      =>  $status == 1 ? 0 : 1,        );        $update =   array(            'lido'      =>  $status,            'data_leitura' =>  $status == 1 ? date('Y-m-d H:i:s') : NULL        );        if($this->update($where, $update)){            return 'ok';        }        else{            return 'error';        }    }}