<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class parametro extends B10_Model{    public function __construct(){        parent::__construct();    }        public function get_valor($parametro){        $resultado = $this->get(array('parametro'=>$parametro));        return @$resultado->valor;    }    public function set_valor($parametro,$valor){        $this->update(array('parametro'=>$parametro),array('valor'=>$valor));    }        public function criar_parametro($parametro,$valor = ''){        $resultado = $this->get(array('parametro'=>$parametro));        if(!is_object($resultado)){            $this->insert(array('parametro'=>$parametro,'valor'=>$valor));        }        else{            $this->set_valor($parametro, $valor);        }    }}