<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class pessoa extends B10_Model{    public function __construct() {        parent::__construct();    }        public function get_all($where = FALSE, $order_by = FALSE, $limit = FALSE) {        $this->ivertex_db = $this->load->database('ivertex',TRUE);        $this->db->where($where);        $this->db->where($order_by);        return $this->ivertex_db->get('tbl_entidades')->result();    }}